  `status` int(10) DEFAULT '0' COMMENT '状态',
  `userId` int(11) DEFAULT NULL COMMENT '当前用户',
  `keyword` varchar(100) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `use_fk` (`userId`),
  CONSTRAINT `use_fk` FOREIGN KEY (`userId`) REFERENCES `user` (`userid`)
) ENGINE=InnoDB AUTO_INCREMENT=116 DEFAULT CHARSET=utf8 COMMENT='社情民意内容';

